import React, { useState, useEffect } from 'react';
import './style.scss';
import Header from './components/Header/Header';
import Scooter from './components/Scooter/Scooter';
import axios from 'axios';


function App() {
    const [bikes, setBikes] = useState([]);
    const [cartItems, setCartItems] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:3000/bikes').then((res) => {
            setBikes(res.data);
        })
    }, []);

    const onAddToCart = (obj) => {
        // Проверяем, есть ли уже такой элемент в корзине
        const isItemInCart = cartItems.some(item => item.id === obj.id);

        // Если элемента нет в корзине, добавляем его
        if (!isItemInCart) {
            axios.post('http://localhost:3000/bikes', obj);  // Отправка на сервер
            setCartItems((prev) => [...prev, obj]);  // Добавляем в состояние корзины
        }
    };

    const onRemoveFromCart = (id) => {
        axios.delete(`http://localhost:3000/bikes/${id}`)
        setCartItems((prev) => prev.filter(item => item.id !== id));
    }

    return (
        <>
            <Header onRemove={onRemoveFromCart} items ={cartItems}/>
            <div className="container">
                <h1 className="scooter-title">ЭЛЕКТРОСАМОКАТЫ</h1>
                <div className="scooterE">
                    {bikes.map((bike) => (
                        <Scooter
                            key={bike.id}
                            title={bike.title}
                            power={bike.power}
                            acc={bike.acc}
                            weight={bike.weight}
                            mileage={bike.mileage}
                            price={bike.price}
                            image={bike.img}
                            onPlus={(obj) => onAddToCart(bike)}
                        />
                    ))}
                </div>
            </div>
        </>
    );
}

export default App;
