import React from 'react';
import './dropdown.scss'

const Dropdown = () => {
    const [dropdown, setDropdown] = React.useState(false);

    const onClickDropdown = () => {
        setDropdown(true);
    }

    return (
        <div className='dropdown'>
            <h1 className="dropdown__mainTitle">
                Отвечаем на вопросы <br/>покупателей
            </h1>
            <div className="dropdowm__block">
                <div className="dropdown__main">
                    <h2 className="dropdown__title">
                        Есть ли гарантия того, что придет именно то, что было заказано? А если я <br/> оплачу и товар не придет?
                    </h2>
                    {dropdown &&
                        <p onClick={onClickDropdown} className="dropdown__subtitle">
                             no
                        </p>
                    }
                </div>
            </div>
        </div>
    );
};

export default Dropdown;
