import React from 'react';
import TopBar from '../TopBar/TopBar';
import './header.scss';
import MidlleBar from "../MidlleBar/MidlleBar";

const Header = () => {

    const [catalog, setCatalog] = React.useState(false)

    function handleCatalogClick() {
        setCatalog(!catalog)
    }

    return (
        <header className="header">
            {catalog && (
                <div onClick={handleCatalogClick} className="midlleBar__overlay">
                    <div onClick={(e) => e.stopPropagation()} className="midlleBar__catalog">
                        asdads
                    </div>
                </div>
            )}
            <div className="container">
                <TopBar />
                <MidlleBar onCatalog = {handleCatalogClick} />
            </div>
        </header>
    );
};

export default Header;
