import React, { useEffect, useState } from 'react';
import './style.scss';
import Header from './components/Header/Header';

function App() {
    const [bikes, setBikes] = useState([]); // создаём состояние для данных

    useEffect(() => {
        fetch('http://localhost:3000/bikes')
            .then(res => res.json())
            .then((data) => {
                setBikes(data); // сохраняем данные в состояние
            });
    }, []);

    return (
        <>
            <Header />
            <h1>Электросамокаты</h1>
            <div className="card">
                {bikes.map((bike) => (
                    <p key={bike.id} className="card-title">
                        {bike.title}
                    </p>
                ))}
            </div>
        </>
    );
}

export default App;
