import React, { useState, useEffect } from 'react';  // Обязательно импортируй useState и useEffect
import './style.scss';
import Header from './components/Header/Header';
import Scooter from './components/Scooter/Scooter';


function App() {
    const [bikes, setBikes] = useState([]);
    const [cartItems, setCartItems] = useState([{
        "id": 1,
        "title": "KuKirin S3 PRO",
        "power": "350w",
        "acc": "7500mAh",
        "weight": "11.9kg",
        "mileage": "30km",
        "price": "20000₽",
        "img": "https://kugoo.kg/storage/vehicles/December2021/XE0cGLkcHYM6Zjqhe54X.jpg"
    }]);

    useEffect(() => {
        fetch('http://localhost:3000/bikes')
            .then(res => res.json())
            .then(data => setBikes(data));
    }, []);

    function onAddToCart (obj) {
        console.log(obj)
    }

    return (
        <>
            <Header items ={cartItems}/>
            <div className="container">
                <h1 className="scooter-title">ЭЛЕКТРОСАМОКАТЫ</h1>
                <div className="scooterE">
                    {bikes.map((bike) => (
                        <Scooter
                            key={bike.id}
                            title={bike.title}
                            power={bike.power}
                            acc={bike.acc}
                            weight={bike.weight}
                            mileage={bike.mileage}
                            price={bike.price}
                            image={bike.img}
                            onPlus={(obj) => onAddToCart(obj)}
                        />
                    ))}
                </div>
            </div>
        </>
    );
}

export default App;
