import React, { useState, useEffect } from 'react';  // Обязательно импортируй useState и useEffect
import './style.scss';
import Header from './components/Header/Header';
import Scooter from './components/Scooter/Scooter';


function App() {
    const [bikes, setBikes] = useState([]);
    const [busket, setBusket] = useState([]);

    useEffect(() => {
        fetch('http://localhost:3000/bikes')
            .then(res => res.json())
            .then(data => setBikes(data));
    }, []);

    function handleToCart(bike) {
        setBusket(prev => [...prev, bike]);
        console.log(setBusket)
    }

    return (
        <>
            <Header />
            <div className="container">
                <h1 className="scooter-title">ЭЛЕКТРОСАМОКАТЫ</h1>
                <div className="scooterE">
                    {bikes.map((bike) => (
                        <Scooter
                            key={bike.id}
                            title={bike.title}
                            power={bike.power}
                            acc={bike.acc}
                            weight={bike.weight}
                            mileage={bike.mileage}
                            price={bike.price}
                            image={bike.img}
                            onAddToCart={() => handleToCart(bike)}
                        />
                    ))}
                </div>
            </div>
        </>
    );
}

export default App;
